let n={},a={};export async function generateWords(t,s){let e=s.language||"english";if(s.mode=="words"){let o=await u(e),r=[];for(let d=0;d<t;d++){let l=Math.floor(Math.random()*o.length);r.push(o[l])}return{words:r}}else{let o=await i(e),r=o[Math.floor(Math.random()*o.length)];return{words:r.text.split(" "),author:r.source}}}async function i(t){if(a[t])return a[t];let e=await(await fetch("quotes/"+t+".json")).json();return a[t]=e.quotes,e.quotes}async function u(t){if(n[t])return n[t];let e=await(await fetch("words/"+t+".json")).json();return e.words.sort(),n[t]=e.words,e.words}
