let wordCache={},quoteCache={};export async function generateWords(e,t){let n=t.language||"english";if(t.mode=="words"){let t=await getWords(n),s=[];for(let n=0;n<e;n++){let o=Math.floor(Math.random()*t.length);s.push(t[o])}return s}let s=await getQuotes(n),o=s[Math.floor(Math.random()*s.length)];return o.text.split(" ")}async function getQuotes(e){if(quoteCache[e])return quoteCache[e];let n=await fetch("quotes/"+e+".json"),t=await n.json();return quoteCache[e]=t.quotes,t.quotes}async function getWords(e){if(wordCache[e])return wordCache[e];let n=await fetch("words/"+e+".json"),t=await n.json();return t.words.sort(),wordCache[e]=t.words,t.words}